mapper.update:
update bbs                      
set ansnum = ansnum + 1        
where grpno =#{grpno} and ansnum > #{ansnum}

mapper.select:
select COUNT(bbsno) as cnt
FROM bbs        
WHERE bbsno=#{bbsno} AND passwd=#{passwd}

mapper.update:
UPDATE bbs  
set
 wname=#{wname},  
 title=#{title},  
 content=#{content} 
 
	 ,filename=#{filename}, 
	 filesize=#{filesize}

WHERE bbsno =#{bbsno}

update bbs

set viewcnt = viewcnt +1
where bbsno = #{bbsno}
