mapper.select:
SELECT
count(*) FROM rmemo
WHERE memono = #{memono}

mapper.update:
		update rmemo
		set content =
		#{content}
		where rnum = #{rnum}

mapper.select:
		select rnum, content, to_char(regdate,'yyyy-mm-dd') regdate, id,
		memono, r
		FROM(
		select rnum, content, regdate, id, memono, rownum r
		FROM(
		select rnum, content, regdate, id, memono
		from Rmemo
		where memono =
		#{memono}
		order by rnum DESC
		)
		)

WHERE r >= #{sno} and r <= #{eno}

select
count(*) from RMEMO
where memono=#{memono}
